/**
 * 把 JSON 字符串写入本地
 *
 * @param jsonText
 * @param filePath
 */
public void writeJSON2Loacl(String jsonText, String filePath) {
    try {
        File file = new File(filePath);
        // 创建上级目录
        if (!file.getParentFile().exists()) {
            file.getParentFile().mkdirs();
        }
        // 如果文件存在，则删除文件
        if (file.exists()) {
            file.delete();
        }
        // 创建文件
        file.createNewFile();
        // 写入文件
        Writer write = new OutputStreamWriter(new FileOutputStream(file), StandardCharsets.UTF_8);
        write.write(jsonText);
        write.flush();
        write.close();
    } catch (Exception e) {
        e.printStackTrace();
    }
}

/**
 * 将文件读取到字符串
 *
 * @param filePath 文件路径
 * @return String 返回文件字符串内容
 */
public static String readFile2String(String filePath) {
    String fileContent = "";
    //文件逐行读入
    try {
        BufferedReader br = new BufferedReader(
                new InputStreamReader(
                        new FileInputStream(filePath)));
        String linestr;//按行读取 将每次读取一行的结果赋值给linestr
        while ((linestr = br.readLine()) != null) {
            fileContent = fileContent + linestr;
        }
        br.close();//关闭IO
    } catch (Exception e) {
        System.out.println("文件操作失败");
        e.printStackTrace();
    }
    return fileContent;
}

/**
  * 保存Bitmap到本地
  * @param inputStream inputStream
  */
public void saveBitmap(InputStream inputStream){
    Bitmap mBitmap = BitmapFactory.decodeStream(inputStream);
    //3.保存Bitmap
    try {
        String savePath = getSDCardPath()+"/AAABBB/";
        File temp = new File(savePath);
        //文件
        String filepath = savePath + "/Screen_1.png";
        File file = new File(filepath);
        if (!temp.exists()) {
            temp.mkdirs();
        }
        if (!file.exists()) {
            file.createNewFile();
        }

        FileOutputStream fos = null;
        fos = new FileOutputStream(file);
        mBitmap.compress(Bitmap.CompressFormat.PNG, 100, fos);
        fos.flush();
        fos.close();
        Toast.makeText(this, "截屏文件已保存至SDCard/AAABBB/下", Toast.LENGTH_LONG).show();

    } catch (Exception e) {
        e.printStackTrace();
    }
}

public ArrayList<CodeSnippets> convertJSON2CodeSnippets(String jsonContent) {
    ArrayList<CodeSnippets> codeSnippetsArrayList = new ArrayList<>();
    JSONArray jsonArray = (JSONArray) JSON.parse(jsonContent);
    codeSnippetsArrayList = (ArrayList<CodeSnippets>) jsonArray.toJavaList(CodeSnippets.class);
    return codeSnippetsArrayList;
}
